function makeGrid(r){for(var n=0;r*r>n;n++)$("#sketch_container").append("<div class='gridUnit'></div>");var i=500/r;$(".gridUnit").css({width:i,height:i,opacity:"0","background-color":"black"})}function pen(){$(".gridUnit").hover(function(){$(this).css({opacity:"1","background-color":"black"})})}function crayon(){function r(){for(var r="0123456789ABCDEF",n="#",i=0;6>i;i++)n+=r[Math.floor(16*Math.random())];return n}$(".gridUnit").hover(function(){$(this).css({opacity:"1","background-color":r()})})}function eraser(){$(".gridUnit").hover(function(){$(this).css({opacity:"0","background-color":"black"})})}function clear_grid(){$(".gridUnit").css({opacity:"0"})}function selectSize(){for(var r=prompt("Enter the number of squares per side; 10-100 works best.");isNaN(r)===!0;)r=prompt("Please enter a number for the grid size");makeGrid(r),pen()}$(document).ready(function(){makeGrid(16),pen()});