function makeGrid(){for(var i=0;gridLength*gridLength>i;i++)$("#sketch_container").append("<div class='gridUnit'></div>");var n=500/gridLength;$(".gridUnit").css({width:n,height:n,display:"inline-block"})}function pen(){$(".gridUnit").hover(function(){$(this).css({opacity:"1","background-color":"black"})})}function crayon(){function i(){for(var i="0123456789ABCDEF",n="#",r=0;6>r;r++)n+=i[Math.floor(16*Math.random())];return n}$(".gridUnit").hover(function(){$(this).css({opacity:"1","background-color":i()})})}function eraser(){$(".gridUnit").hover(function(){$(this).css({opacity:"0","background-color":"black"})})}function clear_grid(){$(".gridUnit").css({opacity:"0"})}function selectSize(){}var gridLength=25;$(document).ready(function(){makeGrid(),pen()});